const express = require('express');
const router = express.Router();
const axios = require('axios');
const cheerio = require('cheerio');
const Sentiment = require('sentiment');
const fs = require('fs');
const path = require('path');
const ScannedNews = require('../models/News');

const sentiment = new Sentiment();

// INITIALIZATION: LOAD TRAINED DATA
let threatDB = {};
try {
  // Load the JSON generated by Python
  const dbPath = path.join(__dirname, '../data/suspicious_keywords.json');
  const rawData = fs.readFileSync(dbPath, 'utf8');
  threatDB = JSON.parse(rawData);
  console.log(`âœ… Multi-Factor Engine Loaded (Source: ${threatDB.meta.source})`);
} catch (err) {
  console.error("âš ï¸ Failed to load Threat DB (Using Fallback)", err.message);
  threatDB = { factors: { topic_triggers: { 'General': ['alien', 'conspiracy'] } } };
}

// Helper: Save to MongoDB
async function saveToDb(text, resultData, userId) {
  try {
    const newRecord = new ScannedNews({
      articleText: text,
      verdict: resultData.verdict,
      confidence: resultData.confidence,
      reasons: resultData.reasons,
      recommendation: resultData.recommendation || '',
      apiUsed: resultData.apiUsed,
      userId: userId || null
    });
    await newRecord.save();
    console.log("Result saved to DB for user:", userId || "Guest");
  } catch (err) { console.error("DB Save Error:", err.message); }
}

router.post('/check', async (req, res) => {
  try {
    let { text, userId } = req.body;
    if (!text || text.length < 50) return res.status(400).json({ msg: 'Text too short.' });

    // 0. SCRAPER MODULE
    if (text.trim().startsWith('http')) {
      console.log("ðŸ”— URL detected. Scraping...");
      try {
        const { data } = await axios.get(text, { headers: { 'User-Agent': 'Mozilla/5.0' } });
        const $ = cheerio.load(data);
        let scraped = "";
        $('p').each((i, el) => { scraped += $(el).text() + " "; });
        if (scraped.length > 50) text = scraped.trim();
      } catch (e) { console.log("âš ï¸ Scraper failed, using raw text."); }
    }

    console.log("âš™ï¸ Running Multi-Factor Analysis...");
    let reasons = [];
    
    // =========================================================
    // ðŸ” FACTOR 1: CONTENT (From ISOT Dataset)
    // =========================================================
    let contentScore = 0;
    const lowerText = text.toLowerCase();
    const topics = threatDB.factors.topic_triggers || {};
    
    Object.keys(topics).forEach(category => {
      const keywords = topics[category];
      const matches = keywords.filter(word => lowerText.includes(word));
      if (matches.length > 5) {
        contentScore += 20;
        reasons.push(`Found high-risk keywords`);
      }
    });

    // =========================================================
    // ðŸŽ¨ FACTOR 2: TONE (Sentiment Analysis)
    // =========================================================
    let toneScore = 0;
    const sentimentResult = sentiment.analyze(text);
    if (sentimentResult.comparative < -0.6) {
      toneScore += 15;
      reasons.push("Tone is significantly aggressive/negative.");
    }

    // =========================================================
    // ðŸ“ FACTOR 3: STYLE (Formatting)
    // =========================================================
    let styleScore = 0;
    const puncCount = (text.match(/[!?]{2,}/g) || []).length;
    if (puncCount > 0) {
      styleScore += 15;
      reasons.push("Sensational punctuation detected.");
    }
    const upperCount = (text.match(/[A-Z]{4,}/g) || []).length;
    if (text.length > 50 && upperCount > 10) {
      styleScore += 15;
      reasons.push("Excessive use of ALL CAPS.");
    }

    // =========================================================
    // ðŸ§® FINAL VERDICT
    // =========================================================
    const totalSuspicion = contentScore + toneScore + styleScore;
    let verdict = "Likely Real";
    let confidence = 80;
    let recommendation = "";

    if (totalSuspicion >= 50) {
      verdict = "Fake / Satire";
      confidence = Math.min(99, 65 + totalSuspicion);
      recommendation = "High risk Detected. Please verify this claim from trusted sources.";
    } else if (totalSuspicion >= 30) {
      verdict = "Unverified";
      confidence = 60;
      recommendation = "Analysis is inconclusive. We recommend checking trusted news outlets for confirmation.";
    } 
    else {
      reasons.push("Content aligns with standard news patterns.");
    }

    const result = {
      verdict, confidence, reasons, recommendation,
      apiUsed: "MultiFactorEngine (ISOT-Trained)"
    };

    await saveToDb(text, result, userId);
    res.json(result);

  } catch (err) {
    console.error("Error:", err.message);
    res.status(500).send('Server Error');
  }
});

// routes/newsRoutes.js

// @route   GET /api/news/history/:userId
// @desc    Get all scans for a specific user
router.get('/history/:userId', async (req, res) => {
  try {
    const { userId } = req.params;
    
    // Find scans by this user, sort by newest first
    const history = await ScannedNews.find({ userId })
      .sort({ submittedAt: -1 })
      .limit(20); // Limit to last 20 scans

    res.json(history);
  } catch (err) {
    console.error("History Error:", err.message);
    res.status(500).send('Server Error');
  }
});

module.exports = router;